		AND DEPT_ID = #{deptId}



		AND DEPT_NAME = #{deptName}

mapper.update:
UPDATE p_un_user_base_info


		USER_NAME = #{userName},


		USER_PWD = #{userPwd},


		USER_ID_CARD_NUMBER = #{userIdCardNumber},


		NAME = #{name},


		USER_BIRTH_PLACE = #{userBirthPlace},


		USER_HOUSEHOLD_REGIST = #{userHouseholdRegist},


		USER_DOMICILE = #{userDomicile},


		USER_OFFICE_PHONE = #{userOfficePhone},


		USER_HOUSE_PHONE = #{userHousePhone},


		MOBILE = #{mobile},


		USER_FAX = #{userFax},


		USER_EMAIL = #{userEmail},


		EMPLOYEE_ID = #{employeeId},


		USER_TITLE = #{userTitle},


		USER_DOSSIER_NUMBER = #{userDossierNumber},


		USER_OFFICE_NUM = #{userOfficeNum}


		GROUP_ID = #{groupId}


		ORG_CODE = #{orgCode}


		USER_STATUS = #{userStatus}



		NUMBER = #{number}


		DEPT_ID = #{deptId}


		DEPT_NAME = #{deptName}


		SIGNATURE_IMG = #{signatureImg}


		USER_HEAD_IMG = #{userHeadImg}


WHERE
USER_ID = #{userId}

mapper.select:
select

	distinct


from p_un_user_base_info




	order by ${orderByClause}


	limit #{start},#{limit}

select p_un_user_base_info.USER_ID, USER_NAME, p_un_user_base_info.USER_PWD, USER_ID_CARD_NUMBER, NAME, USER_BIRTH_PLACE, USER_HOUSEHOLD_REGIST, USER_DOMICILE, USER_OFFICE_PHONE, USER_HOUSE_PHONE, MOBILE, USER_FAX, USER_EMAIL, EMPLOYEE_ID, USER_TITLE,USER_STATUS, USER_DOSSIER_NUMBER, USER_OFFICE_NUM, p_un_user_base_info.LAST_EDIT_TIME, p_un_user_base_info.NUMBER,SIGNATURE_IMG,USER_HEAD_IMG,p_un_group.group_short_name as DEPT_NAME, p_un_group.group_id AS DEPT_ID from p_un_user_base_info left join p_un_user_group on p_un_user_base_info.user_id=p_un_user_group.user_id left join p_un_group on p_un_user_group.group_id=p_un_group.group_id
order by ${orderByClause}
limit #{start},#{limit}

    	select
    	u.*
    	from p_un_user_base_info u
    	inner join p_un_user_role r on u.user_id = r.user_id
    	where r.role_id=#{roleId}

    		and u.name like #{name}


    		and u.GROUP_ID = #{groupId}


				AND u.USER_ID_CARD_NUMBER like #{userIdCardNumber}

    	select
    	u.*,
    	r.group_id deptId
    	from p_un_user_base_info u
    	inner join p_un_user_group r on u.user_id = r.user_id
    	where r.position_id=#{position_id}

    		and u.name like #{name}


    		and u.GROUP_ID = #{groupId}


				AND u.USER_ID_CARD_NUMBER like #{userIdCardNumber}

    	select u.* 
		from p_un_user_base_info u
    	where 1=1 

    		and u.name like #{name}


				AND u.USER_ID_CARD_NUMBER like #{userIdCardNumber}

    	and u.user_id not in 
    	(select r.user_id from p_un_user_role r where r.role_id=#{roleId})
    	and u.GROUP_ID=#{groupId}

    	select u.* 
		from p_un_user_base_info u
    	where 1=1 

    		and u.name like #{name}


				AND u.USER_ID_CARD_NUMBER like #{userIdCardNumber}

    	and u.USER_ID not in(select ug.USER_ID from p_un_user_group ug where 1=1

    		and	ug.GROUP_ID = #{groupId}

    	 )

    		and u.GROUP_ID = #{rootGroupId}

SELECT p_un_user_group.GROUP_ID deptId, p_un_user_base_info.* FROM p_un_user_base_info, p_un_user_group WHERE p_un_user_base_info.USER_ID = p_un_user_group.USER_ID and p_un_user_base_info.group_id=#{groupId}
and p_un_user_group.GROUP_ID=#{deptId}
and p_un_user_base_info.name like #{name}
GROUP BY p_un_user_base_info.USER_ID order by p_un_user_base_info.user_id
